INSERT INTO products VALUES('1J94K63Y9',  'Brittles and toffies',  'Toffee-ettes', 'Danish butter toffee, covered with chocolate and almonds.', 'Irresistible bite-size pieces of rich Danish butter toffee with whole almonds are smothered in Bertha''s creamy milk chocolate, then covered with bits of toasted almonds. Keep a canister on hand for unexpected guests or take one to the office.  1 lb.',  7.25, 16.50  );
