INSERT INTO products VALUES('1U77T63C9',  'Brittles and toffies',  'Sugar Free Peanut Brittle',                 'Our classic peanut brittle, but without sugar.', 'It has the same look and taste as our traditional Peanut Brittle - you won''t be able to tell the difference! Bertha''s Sugar Free Peanut Brittle is scrumptious, sweet and crunchy with the same high quality taste that you''ve come to love from Bertha''s.  1 lb.',  8.25, 18.50  );
INSERT INTO products VALUES('1W96T63B8',  'Brittles and toffies',  'Peanut Butter Brittle Bar',                 'Delicious peanut brittle and rich, creamy milk chocolate.', 'It''s the perfect combination of our famous, old-fashioned, crunchy peanut brittle and rich, creamy milk chocolate. The perfect snack and party treat. Eight individually wrapped 1 oz. bars per box  8 oz.',  4.25,  9.50  );
INSERT INTO products VALUES('2K35T93T5',  'Truffles',              'Pecan Pie Truffles',                        'Rich buttercream with pecans, covered with white chocolate.', 'Our Pecan Pie Truffles start with a rich buttercream filling with only the freshest pecan pieces folded in, then they''re surrounded by Bertha''s white chocolate and topped with an extra large pecan half.  1 lb.', 11.25, 24.00  );
