INSERT INTO products VALUES('8G42K77D3',  'Holiday assortments',  'Christmas Tote',                            'Bertha''s Tote Bag, filled with goodies.', 'If you''re going to tote something around this Christmas, it might as well be Bertha''s candy! We decorated this cute little bag with a gingerbread house that looks good enough to eat! Then we filled it with Bertha''s traditional candy canes, Lollypops and, of course, a Milk Chocolate Santa. Keep the tote once the candy is gone for a Christmas memento or decoration.  5 oz',  3.15,  6.90  );
